import pygameimport pygame.freetypeimport sysimport Playerimport Bulletsimport Initialisationimport Menuimport EnemySlimefrom Initialisation import display, clockfrom EnemySlime import display_scroll, SlimeEnemies, EnemyCordspygame.init()InventoryFunction = Falseplayer_bullets = []keysE = []while True:    display.fill((30, 30, 30))    mouse_x, mouse_y = pygame.mouse.get_pos()    for event in pygame.event.get():        if event.type == pygame.QUIT:            sys.exit()            pygame.QUIT()        if event.type == pygame.MOUSEBUTTONDOWN:            if event.button == 1:                player_bullets.append(Bullets.PlayerBullet(Player.player.x, Player.player.y, mouse_x, mouse_y))                Player.player.mana_usage(15)    keys = pygame.key.get_pressed()    pygame.draw.rect(display, (255, 255, 255), (100 - display_scroll[0], 100 - display_scroll[1], 16, 16))    if keys[pygame.K_UP]:        player.get_health(10)        player.mana_get(10)    if keys[pygame.K_DOWN]:        player.get_damage(10)    if keys[pygame.K_a]:        display_scroll[0] -= 5        Player.player.moving_left = True        for bullet in player_bullets:            bullet.x += 5    else:        Player.player.moving_left = False    if keys[pygame.K_d]:        display_scroll[0] += 5        Player.player.moving_right = True        for bullet in player_bullets:            bullet.x -= 5    else:        Player.player.moving_right = False    if keys[pygame.K_w]:        display_scroll[1] -= 5        Player.player.moving_up = True        for bullet in player_bullets:            bullet.y += 5    else:        Player.player.moving_up = False    if keys[pygame.K_s]:        display_scroll[1] += 5        Player.player.moving_down = True        for bullet in player_bullets:            bullet.y -= 5    else:        Player.player.moving_down = False    Player.player.main(display)    for bullet in player_bullets:        bullet.main(display)    for bullet in player_bullets:        bullet.main(display)    # for enemy in EnemySlime.SlimeEnemies:    #     enemy.main(display)    SlimeEnemies.SlimyMain(display)    Player.player.advanced_mana()    Player.player.advanced_health()    Player.player.mana_regeneration()    Player.player.ShowText_Stats(165, 15, 165, 50, 20, (255, 255, 255), (255, 255, 255))    Player.player.HitBox(EnemyCords, HitboxMain, HitboxSecondary_X, HitboxSecondary_Y)    if InventoryFunction == True:        Menu.playerInventory.clearBackground()        Menu.playerInventory.drawInventory()        Menu.playerInventory.gridInventory()        PLayer.player.ShowText_Stats(262, 712, 262, 742, 20, (255, 30, 30), (0, 127, 255))    if keys[pygame.K_e]:        keysE.append(1)        size = len(keysE)        if size <= 2:            keysE.remove(1)            InventoryFunction = True    if keys[pygame.K_ESCAPE]:        InventoryFunction = False    clock.tick(60)    pygame.display.update()