import pygameimport randomimport Playerdisplay_scroll = [0, 0]class SlimeEnemy:    def __init__(self, x, y):        self.xOffset = None        self.x = x        self.y = y        self.animation_images = [pygame.image.load("enemy_1.png"), pygame.image.load("enemy_2.png"),                                 pygame.image.load("enemy_1.png"), pygame.image.load("enemy_2.png")]        self.animation_count = 0        self.reset_offset = 0        self.offset_x = random.randrange(-300, 300)        self.offset_y = random.randrange(-300, 300)        self.xOffsetBullet = 8        self.yOffsetBullet = 2        self.bulletX = self.x + self.xOffsetBullet        self.bulletY = self.y + self.yOffsetBullet        self.ImageTransformed = False        self.EnemyCords = ()        self.radius = 150        self.radiusDivison = self.radius / 2 + 31    def SlimyMain(self, display):        if self.animation_count + 5 == 16:            self.animation_count = 0        self.animation_count += 1        if self.reset_offset == 0:            self.offset_x = random.randrange(-150, 150)            self.offset_y = random.randrange(-150, 150)            self.reset_offset = random.randrange(120, 150)        else:            self.reset_offset -= 1        if Player.player.x + self.offset_x > self.x - display_scroll[0]:            self.x += 1        elif Player.player.x + self.offset_x < self.x - display_scroll[0]:            self.x -= 1        if Player.player.y + self.offset_y > self.y - display_scroll[1]:            self.y += 1        elif Player.player.y + self.offset_y < self.y - display_scroll[1]:            self.y -= 1        display.blit(pygame.transform.scale(self.animation_images[self.animation_count // 4], (32, 30)),                     (self.x - display_scroll[0], self.y - display_scroll[1]))        self.ImageTransformed = (pygame.transform.scale(self.animation_images[self.animation_count // 4], (32, 30)))        self.EnemyCords = self.ImageTransformed.get_rect()        self.EnemyCords[0] = self.x - display_scroll[0]        self.EnemyCords[1] = self.y - display_scroll[1]        pygame.draw.rect(display, (255, 25, 15), self.EnemyCords, 2)        pygame.draw.circle(display, (255, 0, 0), (self.x - display_scroll[0] + 16, self.y - display_scroll[1] + 15),                           self.radius, 2)        pygame.draw.rect(display, (255, 0, 0), (            self.x - display_scroll[0] + 16 - self.radiusDivison, self.y - display_scroll[1] + 15 - self.radiusDivison, 212,            212),                         2)        pygame.draw.rect(display, (255, 0, 0), (            self.x - display_scroll[0] + 16 - self.radiusDivison - 35,            self.y - display_scroll[1] + 15 - self.radiusDivison + 53,            280, 106), 2)        pygame.draw.rect(display, (255, 0, 0), (self.x - display_scroll[0] + 16 - self.radiusDivison + 52,                                                self.y - display_scroll[1] + 15 - self.radiusDivison + 53 - 87, 106, 280),                         2)        global EnemyCordsTEMP        EnemyCordsTEMP = self.EnemyCords        global EnemyCords_XTEMP        EnemyCords_XTEMP = self.x        global EnemyCords_YTEMP        EnemyCords_YTEMP = self.y        global HitboxMainTEMP        HitboxMainTEMP = (            EnemyCords_XTEMP - display_scroll[0] + 16 - self.radiusDivison,            EnemyCords_YTEMP - display_scroll[1] + 15 - self.radiusDivison,            212, 212)        global HitboxSecondary_XTEMP        HitboxSecondary_XTEMP = (EnemyCords_XTEMP - display_scroll[0] + 16 - self.radiusDivison - 35,                             EnemyCords_YTEMP - display_scroll[1] + 15 - self.radiusDivison + 53, 280, 106)        global HitboxSecondary_YTEMP        HitboxSecondary_YTEMP = (EnemyCords_XTEMP - display_scroll[0] + 16 - self.radiusDivison + 52,                             EnemyCords_YTEMP - display_scroll[1] + 15 - self.radiusDivison + 53 - 87, 106, 280)EnemyCords = EnemyCordsTEMPEnemyCords_X = EnemyCords_XTEMPEnemyCords_Y = EnemyCords_YTEMPHitboxMain = HitboxMainTEMPHitboxSecondary_X = HitboxSecondary_XTEMPHitboxSecondary_YTEMPSlimeEnemies = SlimeEnemy(850, 400)enemies_attack = []SlimeEnemies.GlobalVar()