import pygameimport Initialisationfrom Initialisation import displaypygame.init()class Inventory:    def __init__(self, inventory_spacesX, inventory_spacesY, inventory_width, inventory_heigth):        self.inventory_spacesX = inventory_spacesX        self.inventory_spacesY = inventory_spacesY        self.inventory_width = inventory_width        self.inventory_heigth = inventory_heigth        self.PlayerTextCordsX = 165        self.PlayerTextCordsY = 15        self.GridCordsYChange = 104        self.CurrentGridCordsY = 87        self.LoopCurrentGridCordsY = 191        self.font = pygame.font.Font('alagard.ttf', 50)        self.InventoryText = self.font.render("Player Inventory", True, (255, 255, 255))        self.TextOffsetX = 45        self.TextOffsetY = 25    def drawInventory(self):        pygame.draw.rect(display, (255, 255, 255),                        (self.inventory_spacesX, self.inventory_spacesY, self.inventory_width, self.inventory_heigth),                        3)        pygame.draw.rect(display, (255, 255, 255), (256, 81, 490, 688), 3)        pygame.draw.rect(display, (255, 255, 255), (262, self.CurrentGridCordsY, 478, 100), 2)    def clearBackground(self):        pygame.draw.rect(display, (55, 55, 55), (0, 0, 1500, 850))    # (250, 75, 1000, 700)    def gridInventory(self):        for x in range(5):            y = 0            y = y + x            pygame.draw.rect(display, (255, 255, 255),                            (262, y * self.GridCordsYChange + self.LoopCurrentGridCordsY, 237, 100), 2)            pygame.draw.rect(display, (255, 255, 255),                            (503, y * self.GridCordsYChange + self.LoopCurrentGridCordsY, 237, 100), 2)        display.blit(self.InventoryText, (262 + self.TextOffsetX, self.CurrentGridCordsY + self.TextOffsetY))playerInventory = Inventory(250, 75, 1000, 700)